import { ADDCONTACT, ERROR } from "./Types";

export default (state, action) => {
  switch (action.type) {
    case ADDCONTACT:
      return {
        ...state,
        contacts: [action.payload, ...state.contacts],
        loading: false
      };
    case ERROR:
      return {
        ...state,
        errors: action.payload,
        loading: false
      };
    default:
      return state;
  }
};
